import { Component, Input } from '@angular/core';



@Component({
  selector: 'app-textlog',
  templateUrl: './textlog.component.html',
  styleUrls: ['./textlog.component.css']
})
export class TextlogComponent {


  @Input() nachricht = '';
  timestamp = new Date();
  logEintrag = "[" + this.timestamp.getHours() + ":" + this.timestamp.getMinutes() +":" +this.timestamp.getSeconds() + "] " + this.nachricht + "\n"
  log: any = this.logEintrag;
}
